import React, { useState } from 'react';
import { View, Text, TextInput, StyleSheet, Pressable } from 'react-native';
import Header from './components/Header.js';
import Nav from './components/Nav.js';
import Subfooter from './components/Subfooter.js';
import Footer from './components/Footer.js';

const SignupScreen = ({ navigation }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

 // Function to determine the border color based on the text input value
  const getBorderColor = (value) => {
    return value ? '#27AE60' : 'black';
  };

  return (
    <View>
      <Header />
      <Nav />
      <View style={styles.basic}>
        <Text style={styles.container}>Sign Up</Text>
        <TextInput
          style={[styles.input, { borderColor: getBorderColor(username) }]}
          placeholder="Username"
          placeholderTextColor="gray"
          value={username}
          onChangeText={(text) => setUsername(text)}
        />
        <TextInput
          style={[styles.input, { borderColor: getBorderColor(password) }]}
          placeholder="Password"
          placeholderTextColor="gray"
          value={password}
          onChangeText={(text) => setPassword(text)}
          secureTextEntry={true}
          placeholderStyle={{ fontSize: 16 }}
        />
        <Pressable
          style={({ pressed }) => [
            styles.button,
            {
              backgroundColor: pressed ? 'lightblue' : '#007AFF',
              marginTop: 20,
              elevation: 5,
            },
          ]}
          onPress={() => navigation.navigate('HomeScreen')}
        >
          <Text style={styles.buttonText}>Sign Up</Text>
        </Pressable>
        <Pressable
          style={({ pressed }) => [
            styles.button,
            {
              backgroundColor: pressed ? 'lightblue' : '#007AFF',
              marginTop: 10,
              elevation: 5,
            },
          ]}
          onPress={() => navigation.navigate('Login')}
        >
          <Text style={styles.buttonText}>Back to Login</Text>
        </Pressable>
      </View>

      <Subfooter />
      <Footer />
    </View>
  );
};

const styles = StyleSheet.create({
  basic: {
    alignItems: 'center',

  },
  container:{
  fontSize:30,
  },
  input: {
    marginTop: 10,
    width: '80%',
    padding: 10,
    borderWidth: 1,
    borderRadius: 5,
  },
  button: {
    width: '80%',
    marginBottom: 40,
    padding: 10,
    borderRadius: 5,
  },
  buttonText: {
    color: 'white',
    textAlign: 'center',
    fontSize: 18,
  },
});

export default SignupScreen;
