import React, { useState } from 'react';
import { Text, TextInput, StyleSheet, ScrollView, Pressable } from 'react-native';
import Header from './components/Header.js';
import Nav from './components/Nav.js';
import Subfooter from './components/Subfooter.js';
import Footer from './components/Footer.js';

const LoginScreen = ({ navigation }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [usernameBorderColor, setUsernameBorderColor] = useState('black');
  const [passwordBorderColor, setPasswordBorderColor] = useState('black');

  const handleUsernameFocus = () => {
    setUsernameBorderColor('#27AE60');
  };

  const handleUsernameBlur = () => {
    setUsernameBorderColor('black');
  };

  const handlePasswordFocus = () => {
    setPasswordBorderColor('#27AE60');
  };

  const handlePasswordBlur = () => {
    setPasswordBorderColor('black');
  };

  return (
    <ScrollView>
      <Header />
      <Nav />
      <Text style={styles.container}>
        Login
      </Text>
      <TextInput
        style={[styles.input, { borderColor: usernameBorderColor }]}
        placeholder="Username"
        placeholderTextColor="gray"
        value={username}
        onChangeText={(text) => setUsername(text)}
        onFocus={handleUsernameFocus}
        onBlur={handleUsernameBlur}
      />
      <TextInput
        style={[styles.input, { borderColor: passwordBorderColor }]}
        placeholder="Password"
        value={password}
        onChangeText={(text) => setPassword(text)}
        secureTextEntry={true}
        placeholderTextColor="gray"
        onFocus={handlePasswordFocus}
        onBlur={handlePasswordBlur}
        placeholderStyle={{ fontSize: 16 }}
      />
      <Pressable
        style={({ pressed }) => [
          styles.button,
          {
            backgroundColor: pressed ? 'lightblue' : '#007AFF',
            elevation: 7,
          },
        ]}
        onPress={() => navigation.navigate('HomeScreen')}
      >
        <Text style={styles.buttonText}>Login</Text>
      </Pressable>
      <Pressable
        style={({ pressed }) => [
          styles.button,
          {
            backgroundColor: pressed ? 'lightblue' : '#007AFF',
            elevation: 7,
          },
        ]}
        onPress={() => navigation.navigate('Signup')}
      >
        <Text style={styles.buttonText}>Sign Up</Text>
      </Pressable>
      <Subfooter />
      <Footer />
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    paddingTop: 2,
    alignItems: 'center',
    justifyContent: 'center',
    height: 50,
    marginLeft: 150,
    fontSize: 30,
  },
  input: {
    marginTop: 10,
    width: '80%',
    padding: 10,
    borderWidth: 1,
    borderRadius: 5,
    marginLeft: 30,
  },
  button: {
    marginTop: 20,
    width: '80%',
    marginBottom: 10,
    marginLeft: 30,
    padding: 10,
    borderRadius: 5,
  },
  buttonText: {
    color: 'white',
    textAlign: 'center',
    fontSize: 18,
  },
});

export default LoginScreen;
