import React from 'react';
import { ScrollView, Text, Pressable, StyleSheet, View, Image } from 'react-native';

import Header from './components/Header.js';
import Nav from './components/Nav.js';
import Subfooter from './components/Subfooter.js';
import Footer from './components/Footer.js';
import Prebill from './Prebill.js';
import Retrievebill from './Retrievebill.js';

const Home = ({ navigation }) => {
  const handleButtonPress = (screenName) => {
    navigation.navigate(screenName);
  };

  const buttons = [
    {
      text: 'My Profile',
      imageSource: require('./assets/user.png'),
      screenName: 'Profile',
    },
    {
      text: 'Create a Custom Bill and Generate an Invoice',
      imageSource: require('./assets/invoice.png'),
      screenName: 'Prebill',
    },
    {
      text: 'View Transaction History',
      imageSource: require('./assets/icons8-transaction-history-64.png'),
      screenName: 'Retrievebill',
    },
  ];

  return (
    <ScrollView>
      <View>
        <Header />
        <Nav />
        <View style={styles.body}>
          {/*<Text style={styles.bodyText}>Welcome! Make your selections below:</Text>*/}
          <View style={styles.buttonRow}>
            {buttons.map((button, index) => (
              <Pressable
                key={index}
                style={({ pressed }) => [
                            styles.button,
                            {
                              backgroundColor: pressed ? 'lightblue' : '#007AFF',
                              shadowColor: '#3498DB',
                                                 shadowOffset: {
                                                   width: 0,
                                                   height: 8,
                                                 },
                                                 shadowOpacity: pressed ? 0.8 : 0.5,
                                                 shadowRadius: 10,
                                                 elevation: pressed ? 12 : 10,
                            },
                          ]}
                onPress={() => handleButtonPress(button.screenName)}
              >
                <Image
                  source={button.imageSource}
                  style={styles.buttonImage}
                />
                <Text style={styles.buttonText}>{button.text}</Text>
              </Pressable>
            ))}
          </View>
        </View>
        <Subfooter />
        <Footer />
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  body: {
    alignItems: 'center',
  },
  bodyText: {
    fontSize: 20,
    marginBottom: 20,
    color: '#2C3E50',
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    textAlign: 'center',
  },
 buttonRow: {
     flexDirection: 'row',
     justifyContent: 'center', // Center the buttons horizontally
     flexWrap: 'wrap',
     marginBottom: 20,
   },
   button: {
     backgroundColor: '#3498DB',
     height: 160,
     width: 160,
     marginLeft: 18,
     marginTop: 20,
     marginRight: 17,
     marginBottom: 20,
     padding: 10,
     borderRadius: 80,
     alignItems: 'center',
     justifyContent: 'center',
     shadowColor: '#3498DB',
     shadowOffset: {
       width: 0,
       height: 8,
     },
     shadowOpacity: 0.5,
     shadowRadius: 10,
     elevation: 15,
   },
 buttonImage: {
    width: 60,
    height: 60,
    marginBottom: 10,
  },
});

export default Home;
